import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';
import * as serviceWorker from './serviceWorker';
import { Provider } from 'react-redux'
import thunk from 'redux-thunk'
import { createStore,  applyMiddleware, compose } from 'redux'
import rootReducer from './redux/reducers'
import {sessionService} from 'redux-react-session'
import createAuthRefreshInterceptor from 'axios-auth-refresh';
import axios from 'axios';




const mid = [thunk]
const store = createStore(rootReducer,
    compose(
    applyMiddleware(...mid),
    window.__REDUX_DEVTOOLS_EXTENSION__ && window.__REDUX_DEVTOOLS_EXTENSION__())
    );

const validateSession = (session) => { 
    return new Promise((res, rej)=>{
        if (!session) rej(false)
        const refresh_time = +Object.keys(session).includes("refresh_time") && session.refresh_time;
        if(!refresh_time) rej("time session not found");
        if(new Date().getTime() - refresh_time>60*60*1000) rej("session Expired");
        res(true);
    });
}
const options = { refreshOnCheckAuth: false, redirectPath: '/opt-1', validateSession };
sessionService.initSessionService(store, options)
 



// Function that will be called to refresh authorization
const refreshAuthLogic = failedRequest =>{
    sessionService.loadSession().then( session=>{
        sessionService.loadSession().then(session=>{
           axios.post("https://hi-exchange.com/api/v2/token/refresh/", {
               refresh: session.refresh
           }).then(data=>{
              
               sessionService.saveSession({
                   token: data.data.access,
                   refresh: session.refresh,
                   refresh_time: new Date().getTime()
               }).then(e=>{
                   
                   return Promise.resolve()
               }).catch(e=>{
                console.log(e);
               })
           }).catch(e=>{
               console.log(e);
           })
       }).catch(err=>{
           return Promise.reject()
       })
   }).catch(err=>{
    return Promise.reject()})
} 
// bind axios to auth refresher
createAuthRefreshInterceptor(axios, refreshAuthLogic)


ReactDOM.render(
    <Provider store={store}>
        <App />
    </Provider>

, document.getElementById('root'));

// If you want your app to work offline and load faster, you can change
// unregister() to register() below. Note this comes with some pitfalls.
// Learn more about service workers: https://bit.ly/CRA-PWA
serviceWorker.unregister();
