import React, {useState, useEffect} from 'react'
import { useSelector } from 'react-redux';

function Timer() {
    const [time, setTime] = useState(60)
    const [next, setNext] = useState("0:0:0")
    const next_refresh = useSelector(state=>state.indexConverter.nextRefreshTime)
    useEffect(() => {
        setNext(next_refresh)
        console.log(next_refresh);
        
    }, [next_refresh])
    const nd = new Date()
    const [h, m, s] = next.split(':')
    nd.setHours(+h)
    nd.setMinutes(+m)
    nd.setSeconds(+s)
    setTimeout(e=>{
        setTime( Math.round( (nd - new Date())/1000 ) )
    }, 1000)
    return (
        <div>
            {(time < 60 && time >=0) ? time : ""}
        </div>
    )
}

export default Timer
