import React, {useEffect, useState} from 'react'
import TradingViewWidget from 'react-tradingview-widget';
import { Themes } from 'react-tradingview-widget';
import axios from 'axios';

function ProfileChart({ selectedChart}) {
    const base = "https://pro-api.coinmarketcap.com/v1/cryptocurrency/quotes/latest"
    const apikey = "8a00c428-798d-4461-99cc-3aa30573fc3f"
    const [info, setInfo] = useState({})
    useEffect(() => {
        
        axios.get(base + "?symbol="+selectedChart+"&CMC_PRO_API_KEY="+apikey,)
        .then(res=>{
            const {data} = res.data;
            setInfo(data);
        }).catch(err=>{
            console.log(err);
            
        })
    }, [])    
    return (
        <div className="card profile_chart transparent">
            <div className="card-body">
                {/* <AreaChart /> */}
                <TradingViewWidget 
                    symbol={selectedChart}
                    theme={Themes.DARK}
                    locale="fa_IR"
                    width={"100%"}
                    height={400}
                    hide_top_toolbar={true}
                    allow_symbol_change={false}
                    withdateranges={true}
                    />
                <div className="chart-content text-center mt-3">
                    <div className="row">
                        <div className="col-xl-4 col-sm-6 col-6">
                            
                                <div className="chart-stat">
                                    <p className="mb-1">حجم 24 ساعته</p>
                                    { Object.keys(info).length > 0? 
                                        <strong>{"test"}</strong>
                                        :"در حال دریافت"
                                    }
                                </div>
                                
                        </div>
                        <div className="col-xl-4 col-sm-6 col-6">
                            <div className="chart-stat">
                                <p className="mb-1">کپ بازار</p>
                                <strong>19B USD</strong>
                            </div>
                        </div>
                        <div className="col-xl-4 col-sm-6 col-6">
                            <div className="chart-stat">
                                <p className="mb-1">گردش</p>
                                <strong>29.4M BTC</strong>
                            </div>
                        </div>
                        <div className="col-xl-4 col-sm-6 col-6">
                            <div className="chart-stat">
                                <p className="mb-1">های کل</p>
                                <strong>19.783.06 USD</strong>
                            </div>
                        </div>
                        <div className="col-xl-4 col-sm-6 col-6">
                            <div className="chart-stat">
                                <p className="mb-1">تییکال هلد</p>
                                <strong>88 days</strong>
                            </div>
                        </div>
                        <div className="col-xl-4 col-sm-6 col-6">
                            <div className="chart-stat">
                                <p className="mb-1">فعالیت معالمه</p>
                                <strong>70% buy </strong>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default ProfileChart
