import {LOADER_ON, LOADER_OFF} from '../actions'
import { bindActionCreators } from 'C:/Users/Abbas/AppData/Local/Microsoft/TypeScript/3.5/node_modules/redux';

const init_state = {
    loading: false
}

export const preloaderReducer = (state=init_state, action)=>{
    switch (action.type) {
        case LOADER_ON:
            return {loading: true}
        case LOADER_OFF:
            return {loading: false}
        default:
            return state
    }
}
