import React, {useEffect, useState} from 'react'
import TradingViewWidget from 'react-tradingview-widget';
import { Themes } from 'react-tradingview-widget';
import axios from 'axios';
import Loader from 'react-loader-spinner'
function ProfileChart({ selectedChart}) {
    const base = "http://127.0.0.1:5000/cryptocurrency/quotes/latest?symbol="
    const [info, setInfo] = useState({})
    useEffect(() => {
        setInfo({})
        axios.get(base + selectedChart)
        .then(res=>{
            const {data} = res.data;
            setInfo(data);
            
        }).catch(err=>{
            console.log(err);
            
        })
    }, [selectedChart])    
    return (
        <div className="card profile_chart transparent">
            <div className="card-body">
                {/* <AreaChart /> */}
                <div style={{minHeight: 400+"px"}}>

                <TradingViewWidget 
                    symbol={selectedChart}
                    theme={Themes.DARK}
                    locale="fa_IR"
                    width={"100%"}
                    height={400}
                    hide_top_toolbar={true}
                    allow_symbol_change={false}
                    withdateranges={true}
                    />
                </div>
                <div className="chart-content text-center mt-3">
                    <div className="row">
                    <div className="col-xl-4 col-sm-6 col-6">
                            <div className="chart-stat">
                                <p className="mb-1">قیمت</p>
                                { Object.keys(info).includes(selectedChart) ? 
                                    <strong dir="ltr">{Number(info[selectedChart]["quote"]["USD"]["price"]).toLocaleString()}$</strong>
                                    :<Loader type="TailSpin" color="#00BFFF" height={20} width={80} />
                                }
                            </div>
                        </div>
                        <div className="col-xl-4 col-sm-6 col-6">
                            <div className="chart-stat">
                                <p className="mb-1">حجم 24 ساعته</p>
                                { Object.keys(info).length > 0? 
                                    <strong dir="ltr">{Number(info[selectedChart]["quote"]["USD"]["volume_24h"]).toLocaleString()}$</strong>
                                    :<Loader type="TailSpin" color="#00BFFF" height={20} width={80} />
                                }
                            </div>
                        </div>
                        <div className="col-xl-4 col-sm-6 col-6">
                            <div className="chart-stat">
                                <p className="mb-1">حجم بازار</p>
                                { Object.keys(info).length > 0? 
                                    <strong  dir="ltr">{Number(info[selectedChart]["quote"]["USD"]["market_cap"]).toLocaleString()} $</strong>
                                    :<Loader type="TailSpin" color="#00BFFF" height={20} width={80} />
                                }
                            </div>
                        </div>
                        <div className="col-xl-4 col-sm-6 col-6">
                            <div className="chart-stat">
                                <p className="mb-1">درصد تغییر 1 ساعته</p>
                                { Object.keys(info).length > 0? 
                                    <strong dir="ltr">{Number(info[selectedChart]["quote"]["USD"]["percent_change_1h"]).toLocaleString()}%</strong>
                                    :<Loader type="TailSpin" color="#00BFFF" height={20} width={80} />
                                }
                            </div>
                        </div>
                        <div className="col-xl-4 col-sm-6 col-6">
                            <div className="chart-stat">
                                <p className="mb-1">درصد تغییر 24 ساعته</p>
                                { Object.keys(info).length > 0? 
                                    <strong dir="ltr">{Number(info[selectedChart]["quote"]["USD"]["percent_change_24h"]).toLocaleString()}%</strong>
                                    :<Loader type="TailSpin" color="#00BFFF" height={20} width={80} />
                                }
                            </div>
                        </div>
                        <div className="col-xl-4 col-sm-6 col-6">
                            <div className="chart-stat">
                            <p className="mb-1">درصد تغییر 7 روزه</p>
                                { Object.keys(info).length > 0? 
                                    <strong dir="ltr">{Number(info[selectedChart]["quote"]["USD"]["percent_change_7d"]).toLocaleString()}%</strong>
                                    :<Loader type="TailSpin" color="#00BFFF" height={20} width={80} />
                                }
                            </div>
                        </div>
                        
 
                    </div>
                </div>
            </div>
        </div>
    )
}

export default ProfileChart
