import {
    FETCH_USER_DETAIL,
    UPDATE_USER_DETAIL,
    UPDATE_USER_PROFILE,
    CHECK_USER_SESSION,
    UPDATE_AUTH_ID,
    UPDATE_LOG_STATUS,
    UPDATE_LOG_INFO,
    UPDATE_USER_WALLET
} from '../actionTypes'
import axios from 'axios';

export const udpate_user_detail = (user)=>{
    return {
        type: UPDATE_USER_DETAIL,
        payload: user
    }
}
export const udpate_user_profile = (profile)=>{
    return {
        type: UPDATE_USER_PROFILE,
        payload: profile
    }
}

export const udpate_user_wallet = (wallet)=>{
    return {
        type: UPDATE_USER_WALLET,
        payload: wallet
    }
}
export const update_auth_id = (authID)=>{
    return {
        type: UPDATE_AUTH_ID,
        payload: authID
    }
}
export const update_log_status = (status)=>{
    return {
        type: UPDATE_LOG_STATUS,
        payload: status
    }
}
export const update_log_info = (info)=>{
    return {
        type: UPDATE_LOG_INFO,
        payload: info
    }
}

export const fetch_user_detail = (token)=>{
    return (dispatch) => {
        axios.get("https://hi-exchange.com/api/v2/account/details/", {
            headers: {
                "Authentication": "Bearer "+token,

            }
        }).then(data=>{
            console.log(data);
        }).catch(err=>{
            console.log(err);
        })
    }
}
export const fetch_user_profile = (token)=>{
    return (dispatch) => {
        axios.get("https://hi-exchange.com/api/v2/account/profile/", {
            headers: {
                "Authentication": "Bearer "+token
            }
        }).then(data=>{
            console.log(data);
        }).catch(err=>{
            console.log(err);
        })
    }
}
export const check_user_session = ()=>{
    return (dispatch) => {
        const token = localStorage.getItem("hiexchange_token");
        if(token){
            const refresh = localStorage.getItem("hiexchange_refresh");
            dispatch(update_log_info({token, refresh}));
            dispatch(fetch_user_detail(token));
            dispatch(fetch_user_profile(token));
        }
    }
}
