import React, { useEffect, useState } from 'react'
import { useSelector, useDispatch } from 'react-redux';
import RadialChart from "../charts/radial";
import { get_wallet_list } from '../../redux/actions';
import Loader from 'react-loader-spinner';

function Balance() {
    const dispatch = useDispatch()
    const wallet = useSelector(state=> state.wallet);
    useEffect(()=>{
        dispatch(get_wallet_list());
    }, [])
    // <h4 className="mt-5">موجودی کل : <strong>${totalBalance}</strong></h4>     
    //         <Link to="/wallet" className="text-center w-100 d-block">شارژ کیف پول</Link>
    return (
        <div className="row">
            <div className="col-xl-12 col-xxl-6">
                <div className="card balance-widget transparent">
                    <div className="card-body p-0">
                    {wallet.is_fetching?
                        <Loader type="Oval" color="#00BFFF" height={80} width={80} />
                        :
                        <div className="row">
                            <div className="col-xl-12 col-xxl-6 col-lg-6 px-0">
                                <RadialChart />
                            </div>
                            <div className="col-xl-12 col-xxl-6 col-lg-6">
                            <div className="balance-widget mt-5">
                                <ul className="list-unstyled">
                                {!wallet.wallet.length ?
                                    <li className="list-unstyled">
                                        در حال حاضر ارزی در کیف پول خود ندارید.
                                        <br/><br/>
                                        <button className="text-secondary border-0 bg-transparent">شارژ کیف پول</button>
                                    </li>
                                    :
                                    wallet.map((item, idx)=>{
                                        return <li key={idx} className="d-flex">
                                            <i className={"cc "+ item.service.small_name_slug + " ms-3"}></i>
                                            <div className="flex-grow-1">
                                                <h5 className="m-0">{item.service.small_name_slug}</h5>
                                            </div>
                                            <div className="text-right">
                                                <h5>{item.balance} {" "} {item.service.small_name_slug}</h5>
                                                {/* <span>0.125 USD</span> */}
                                            </div>
                                        </li>
                                    })
                                }
                                </ul>
                            </div>
                            </div>
                        </div> 
                    }
                    </div>
                </div>
            </div>

        </div>
    )
}

export default Balance
