import {
    FETCH_USER_DETAIL,
    UPDATE_USER_DETAIL,
    UPDATE_WALLET
} from '../actionTypes'
import axios from 'axios';

export const udpate_user = (user)=>{
    return {
        type: UPDATE_USER_DETAIL,
        payload: user
    }
}
export const udpate_wallet = (wallet)=>{
    return {
        type: UPDATE_WALLET,
        payload: wallet
    }
}
export const fetch_user_detail = (token)=>{
    return (dispatch) => {
        axios.get("https://hi-exchange.com/api/v2/account/details/", {
            headers: {
                "Authentication": "Bearer "+token
            }
        })
    }
}
