import React, { useState, useContext } from 'react';
 import { Link } from 'react-router-dom';
import { Row, Col, Card } from 'react-bootstrap'
import { right } from '@popperjs/core';
import { UserContext } from '../UserContext';
import { get } from 'http';

function getCurrencyAmount(fullName) {
    return Math.random()*10;
}

function Convert() {
    const [convertAmount, setConvertAmount] = useState(0);
    const [karmozdAmount, setKarmozdAmount] = useState(0);
    const [currencyFrom, setCurrencyFrom] = useState(null);
    const [currencyTo, setCurrencyTo] = useState(null);
    const [currencyAvailable, setCurrencyAvailable] = useState(0);
    const [currencyResult, setCurrencyResult] = useState();
    const { currencyList, karmozd } = useContext(UserContext);
    
    const currencyOptions =  currencyList.map((c, idx)=>{
        return <option key={idx} value={c.fullName}> {c.persianName}</option>
    });

    const changeConvertAmount = e=>{
        let enteredValue = e.target.value;
        if (isNaN(+enteredValue)) enteredValue = 0;
        if(currencyAvailable)
        setConvertAmount(Math.min(enteredValue, currencyAvailable));
    }
    const changeCurrencyFrom = (e)=>{
        let selectedCurrency = e.target.value;
        setCurrencyFrom(selectedCurrency);
        setCurrencyAvailable (getCurrencyAmount(selectedCurrency) );        
        setConvertAmount(Math.min(convertAmount, currencyAvailable));
    }
    const changeCurrencyTo = (e)=>{
        let selectedCurrency = e.target.value;
        setCurrencyTo(selectedCurrency);

    }
    return (
        <>
            <style type="text/css">
                {`
                    .form-label{
                        padding-right: 4px;
                    }
                `}
            </style>
            
            <Card>
                <Card.Header >
                    <Card.Title>تبدیل</Card.Title>
                </Card.Header>
                <Card.Body>
                    <div className="buy-sell-widget">
                        <form method="post" name="myform" className="currency_validate row">
                            <div className="mb-3 col-xl-4 mb-0">
                                <label className="form-label" htmlFor="currency_amount">مقدار</label>
                                <input type="text" name="currency_amount" className="form-control" value={convertAmount} onChange={ changeConvertAmount }
                                    placeholder="100" />          
                            </div>

                            <div className="mb-3 col-xl-4 mb-0">
                                <label className="form-label">ارز </label>
                                <select name='currency' className="form-control" onChange={changeCurrencyFrom} value={currencyFrom}>
                                    <option value={null}>انتخاب</option>
                                    { currencyOptions }
                                    {/* <option value="bitcoin">بیت کوین</option>
                                    <option value="litecoin">لایت کوین</option> */}
                                </select>
                            </div>
                            <div className="mb-3 col-xl-4 mb-0">
                                <label className="form-label">را به ارز </label>
                                <select name='currency' className="form-control" onChange={changeCurrencyTo} value={currencyTo}>
                                    <option value={null}>انتخاب</option>
                                    { currencyOptions }
                                </select>
                            </div>

                            <div className="col-12 row">
                                <div className=" col-xl-4 mb-0">
                                    <label className="form-label" htmlFor="currency_amount_available">موجودی</label>
                                    <input type="text" name="currency_amount_available" className="form-control pe-none" value={currencyAvailable} 
                                        placeholder="" /> 
                                </div>
                                <div className="col-6 align-items-end d-flex px-0">
                                    { currencyFrom &&
                                        <button type="button" className="bg-transparent border-0 text-white px-2" onClick={e=>setConvertAmount(currencyAvailable)}>انتخاب کل موجودی</button>
                                    }
                                </div>
                            </div>
                            <div className="col-12 row">
                                <div className=" col-xl-4 mb-0">
                                    <label className="form-label" htmlFor="karmozd">کارمزد</label>
                                    <input type="text" name="karmozd" className="form-control pe-none " value={karmozdAmount} 
                                        placeholder="" /> 
                                </div>
                                
                            </div>

                            <div className="col-12">
                                <div className="d-flex justify-content-between align-items-center mb-3">
                                    <h6 className="mb-0">$49750 باقیمانده</h6>
                                    <button type="submit" name="submit"
                                        className="btn btn-success px-5" disabled={convertAmount > 0}>تبدیل کن</button>
                                </div>
                            </div>


                        </form>
                    </div>
    
                </Card.Body>
            </Card>
        </>
    )
}
export default Convert;
