import React, { Component } from "react";
import ReactApexChart from "react-apexcharts";
import { json } from "stream/consumers";



class RadialChart extends Component {
    constructor(props) {
        super(props);

        this.state = {

            series: [76, 67, 61, 90],
            options: {
                chart: {
                    height: 300,
                    type: 'radialBar',
                    width: 300,
                    
                },
                tooltip: {
                    enabled: true,
                    enabledOnSeries: undefined,
                    shared: true,
                    followCursor: false,
                    intersect: false,
                    inverseOrder: false,
                    
                    fillSeriesColor: false,
                    theme: "light",
                    style: {
                      fontSize: '14px',
                      fontFamily: undefined,
                      justifyContent: 'space-between'
                    },
                    onDatasetHover: {
                        highlightDataSeries: false,
                    },
                    x: {
                        show: true,
                        format: 'dd MMM',
                        formatter: undefined,
                    },
                    y: {
                        formatter: undefined,
                        title: {
                            formatter: (seriesName) => seriesName,
                        },
                    },
                    z: {
                        formatter: undefined,
                        title: 'Size: '
                    },
                    marker: {
                        show: true,
                    },
                    items: {
                       display: "flex",
                    },
                    fixed: {
                        enabled: false,
                        position: 'topRight',
                        offsetX: 0,
                        offsetY: 0,
                    },
                    custom: function({series, seriesIndex, dataPointIndex, w}) {
                        return '<div class="arrow_box">' +
                          '<span>' + series[seriesIndex] +   '</span>' +
                          '</div>'
                      }
                },
                plotOptions: {
                    radialBar: {
                        offsetY: 0,
                        startAngle: 0,
                        endAngle: 360,
                        hollow: {
                            margin: 5,
                            size: '30%',
                            background: 'transparent',
                            image: undefined,
                        },
                        dataLabels: {
                            name: {
                                show: false,
                            },
                            value: {
                                show: false,
                            }
                        }
                    }
                },
                colors: [
                    'rgba(137, 22, 255,1)',
                    'rgba(137, 22, 255,0.7)',
                    'rgba(137, 22, 255,0.3)',
                    'rgba(137, 22, 255,0.1)'
                ],
                labels: ['Bitcoin', 'Litecoin', 'Ripple', 'Dash'],
                legend: {
                    show: false,
                    floating: true,
                    fontSize: '16px',
                    position: 'left',
                    offsetX: 160,
                    offsetY: 15,
                    labels: {
                        useSeriesColors: true,
                    },
                    markers: {
                        size: 0
                    },
                    formatter: function (seriesName, opts) {
                        return seriesName + ":  " + opts.w.globals.series[opts.seriesIndex]
                    },
                    itemMargin: {
                        vertical: 3,
                        horizontal: 3
                    }
                },
                responsive: [{
                    breakpoint: 480,
                    options: {
                        legend: {
                            show: false
                        }
                    }
                }]
            },


        };
    }



    render() {
        return (


            <ReactApexChart options={this.state.options} series={this.state.series} type="radialBar" height={360} />



        );
    }
}

export default RadialChart;
