import React, { Component, useContext, useEffect, useState } from 'react';
import { BrowserRouter, Route, Switch, Redirect } from 'react-router-dom';
import Dashboard from './pages/index';
import BuySell from './pages/buy-sell';
import Accounts from './pages/accounts';
import Settings from './pages/settings';
import Preferences from './pages/settings-preferences';
import SettingsSecurity from './pages/settings-security';
import SettingsAccount from './pages/settings-account';
import AddBankAccount from './pages/add-bank-acc';
import AddDebitCard from './pages/add-debit-card';
import Locked from './pages/lock';
import Otp1 from './pages/otp-1';
import Otp2 from './pages/otp-2';
import PrivacyPolicy from './pages/privacy-policy';
import Reset from './pages/reset';
import Signin from './pages/signin';
import Signup from './pages/signup';
import TermsCondition from './pages/terms-condition';
import VerifyStep1 from './pages/verify-step-1';
import VerifyStep2 from './pages/verify-step-2';
import VerifyStep3 from './pages/verify-step-3';
import VerifyStep4 from './pages/verify-step-4';
import VerifyStep5 from './pages/verify-step-5';
import VerifyStep6 from './pages/verify-step-6';
import History from './pages/history';
import Demo from './pages/demo';
import Landing from './pages/landing';
import {UserContext} from './UserContext';
import { useDispatch, useSelector, useStore } from 'react-redux'
import { fetch_currencies, check_user_session } from '../redux/actions'
import 'react-toastify/dist/ReactToastify.css';

function getCurrentUser() {
    return {
        name: "میلاد عبادی",
        dollarCredit: 10000,
        account:{
            accountVerified: true,
            twoStepVerification: false,
            firstDeposit: false
        },
        currencies:{
            BTC: 10.56,
            LTC: 5.6,
            WTC: 10.899,
        },
    }
}
function  getKarmozd(){
    return 0.01;
}

function getProtectedRoute(path, component, user, justNotLogged=false) {
    if (!user.isLogged && !justNotLogged)
      return <Redirect exact path={path} to="/otp-1" />;
    return <Route exact path={path} component={component} />;
}
const Index = ()=> {
    const user = useSelector(state=>state.userManager)
    const dispatch = useDispatch();

    
    useEffect( ()=>{
        dispatch(check_user_session());
        dispatch(fetch_currencies());
    }, []);
    
    return (
        <>
            <BrowserRouter basename={'/cheerio-react/'}>
                <div id="main-wrapper">
                    
                    <Switch>
                        {getProtectedRoute('/', Dashboard, user )}
                        {getProtectedRoute('/buy-sell', BuySell, user )}
                        {getProtectedRoute('/accounts', Accounts, user )}
                        {getProtectedRoute('/settings', Settings, user )}
                        {getProtectedRoute('/settings-preference', Preferences, user )}
                        {getProtectedRoute('/settings-security', SettingsSecurity, user )}
                        {getProtectedRoute('/settings-account', SettingsAccount, user )}
                        {getProtectedRoute('/add-bank-acc', AddBankAccount, user )}
                        {getProtectedRoute('/add-debit-card', AddDebitCard, user )}
                        {getProtectedRoute('/lock', Locked, user )}
                        {getProtectedRoute('/otp-1', Otp1, user, true)}
                        {getProtectedRoute('/otp-2', Otp2, user, true )}
                        {getProtectedRoute('/privacy-policy', PrivacyPolicy, user )}
                        {getProtectedRoute('/reset', Reset, user )}
                        {getProtectedRoute('/signin', Signin, user, true)}
                        {getProtectedRoute('/signup', Signup, user, true)}
                        {getProtectedRoute('/terms-condition', TermsCondition, user )}
                        {getProtectedRoute('/verify-step-1', VerifyStep1, user )}
                        {getProtectedRoute('/verify-step-2', VerifyStep2, user )}
                        {getProtectedRoute('/verify-step-3', VerifyStep3, user )}
                        {getProtectedRoute('/verify-step-4', VerifyStep4, user )}
                        {getProtectedRoute('/verify-step-5', VerifyStep5, user )}
                        {getProtectedRoute('/verify-step-6', VerifyStep6, user )}
                        {getProtectedRoute('/history', History, user )}
                        {getProtectedRoute('/landing', Landing, user )}
                        {getProtectedRoute('/demo', Demo, user )}
                        {/* <Route path='/' exact component={Dashboard} /> */}
                        {/* <Route path='/buy-sell' component={BuySell} />
                        <Route path='/accounts' component={Accounts} />
                        <Route path='/settings' component={Settings} /> */}
                        {/* <Route path='/settings-preferences' component={Preferences} />
                        <Route path='/settings-security' component={SettingsSecurity} />
                        <Route path='/settings-account' component={SettingsAccount} /> */}
                        {/* <Route path='/add-bank-acc' component={AddBankAccount} /> */}
                        {/* <Route path='/add-debit-card' component={AddDebitCard} />
                        <Route path='/lock' component={Locked} /> */}
                        {/* <Route path='/otp-1' component={Otp1} />
                        <Route path='/otp-2' component={Otp2} /> */}
                        {/* <Route path='/privacy-policy' component={PrivacyPolicy} />
                        <Route path='/reset' component={Reset} />
                        <Route path='/signin' component={Signin} />
                        <Route path='/signup' component={Signup} /> */}
                        {/* <Route path='/terms-condition' component={TermsCondition} /> */}
                        {/* <Route path='/verify-step-1' component={VerifyStep1} />
                        <Route path='/verify-step-2' component={VerifyStep2} />
                        <Route path='/verify-step-3' component={VerifyStep3} />
                        <Route path='/verify-step-4' component={VerifyStep4} />
                        <Route path='/verify-step-5' component={VerifyStep5} />
                        <Route path='/verify-step-6' component={VerifyStep6} /> */}
                        {/* <Route path='/history' component={History} />
                        <Route path='/demo' component={Demo} />
                        <Route path='/landing' component={Landing} /> */}
                    </Switch>
                </div>
            </BrowserRouter>

        </>
    );
    
}

export default Index;
