import React, { useEffect, useState } from 'react'
import { useSelector } from 'react-redux';
import RadialChart from "../charts/radial";

function Balance() {
    const currentUser = useSelector(state=>state.session.user);
    const Wallet = useSelector(state=> state.wallet.wallet);
    const [ wallet, setWallet] = useState([])
    useEffect(()=>{
        setWallet(Wallet);
    }, [Wallet])
    // <h4 className="mt-5">موجودی کل : <strong>${totalBalance}</strong></h4>     
    //         <Link to="/wallet" className="text-center w-100 d-block">شارژ کیف پول</Link>
    return (
        <div className="row">
            <div className="col-xl-12 col-xxl-6">
            <div className="card balance-widget transparent">
                <div className="card-body p-0">
                <div className="row">
                    <div className="col-xl-12 col-xxl-6 col-lg-6 px-0">
                        <RadialChart/>
                    </div>
                    <div className="col-xl-12 col-xxl-6 col-lg-6">
                    <div className="balance-widget mt-5">
                        <ul className="list-unstyled">
                        {!wallet ?
                            <li className="list-unstyled">
                                در حال حاضر ارزی در کیف پول خود ندارید.
                            </li>
                            :
                            wallet.map((item, i)=>{
                                return <li key={i} className="d-flex">
                                    <i className={"cc "+ item.small_name_slug + " ms-3"}></i>
                                    <div className="flex-grow-1">
                                        <h5 className="m-0">{item.small_name_slug}</h5>
                                    </div>
                                    <div className="text-right">
                                        <h5>{item.balance} {" "} {item.small_name_slug}</h5>
                                        {/* <span>0.125 USD</span> */}
                                    </div>
                                </li>
                            })
                        }
                        </ul>
                    </div>
                    </div>
                </div>
                </div>
            </div>
            </div>

      </div>
    )
}

export default Balance
