import {
    FETCH_USER_DETAIL,
    UPDATE_USER_DETAIL,
    UPDATE_USER_PROFILE,
    CHECK_USER_SESSION,
    REFRESH_USER_TOKEN,
    UPDATE_AUTH_ID,
    UPDATE_LOG_STATUS,
    UPDATE_LOG_INFO,
    UPDATE_USER_WALLET
} from '../actionTypes'
import axios from 'axios';
import Cookies from 'universal-cookie';
import { COOKIE_MAX_AGE } from '../constants'
export const udpate_user_detail = (user)=>{
    return {
        type: UPDATE_USER_DETAIL,
        payload: user
    }
}
export const udpate_user_profile = (profile)=>{
    return {
        type: UPDATE_USER_PROFILE,
        payload: profile
    }
}

export const udpate_user_wallet = (wallet)=>{
    return {
        type: UPDATE_USER_WALLET,
        payload: wallet
    }
}
export const update_auth_id = (authID)=>{
    return {
        type: UPDATE_AUTH_ID,
        payload: authID
    }
}
export const update_log_status = (status)=>{
    return {
        type: UPDATE_LOG_STATUS,
        payload: status
    }
}
export const update_log_info = (info)=>{
    return {
        type: UPDATE_LOG_INFO,
        payload: info
    }
}

export const fetch_user_detail = (token)=>{
    return (dispatch) => {
        axios.get("https://hi-exchange.com/api/v2/account/details/", {
            headers: {
                "Authorization": "Bearer "+token,

            }
        }).then(data=>{
            console.log(data);
        }).catch(err=>{
            console.log(err);
        })
    }
}
export const fetch_user_profile = (token)=>{
    return (dispatch) => {
        axios.get("https://hi-exchange.com/api/v2/account/manage/", {
            headers: {
                "Authorization": "Bearer "+token
            }
        }).then(data=>{
            console.log(data);
        }).catch(error=>{
            console.log(error.response.status);
        })
    }
}

export const check_user_session = ()=>{
    return dispatch => {
        const cookies = new Cookies();
        const token = cookies.get("hiexchange_token");
        const refresh_time = +cookies.get("hiexchange_refresh_time");
        const refresh = cookies.get("hiexchange_refresh");
        
        if(token && refresh_time && refresh){
            const now = new Date()
            if (now.getTime() - refresh_time > 60*30*1000){
                axios.post("https://hi-exchange.com/api/v2/token/refresh/", {
                    refresh: refresh
                }).then(data=>{
                    cookies.set("hiexchange_token", data.access);
                    cookies.set("hiexchange_refresh_time", now.getTime());
                    dispatch(update_log_info({token, refresh}));
                    dispatch(fetch_user_detail(token));
                    dispatch(fetch_user_profile(token));
                }) 
            }else{
                dispatch(update_log_info({token, refresh}));
                dispatch(fetch_user_detail(token));
                dispatch(fetch_user_profile(token));
            }
        }
    }
}
