import axios from 'axios';
import { resolve } from './resolve.js';
import React from 'react'

const get_token= async ()=>{
  const token = localStorage.getItem("hiexchange_token");
  const refresh_time = +localStorage.getItem("hiexchange_refresh_time");
  const refresh = localStorage.getItem("hiexchange_refresh");
  if(token && refresh_time && refresh){
    const now = new Date()
    if (now.getTime() - refresh_time > 60*30*1000){
        await fetchApi("post", "/api/v2/token/refresh/",{refresh}, true )
        .then(data=>{
          localStorage.setItem("hiexchange_token", data.data.access);
          localStorage.setItem("hiexchange_refresh_time", new Date().now());
          return token
        })
    }else{
        return token
    }
  }else{
      return undefined
  }
}
export async function fetchApi(method, url, data, is_refresh=false){
  const base_url = "https://hi-exchange.com/"
  await get_token().then(token=>{
      return  axios({
        method,
        url: base_url+url,
        data,
        headers:{
            "Authorization": "Bearer "+ token
        }
      })
  })
 
  
}
