import React, {useEffect, useState} from 'react'
import TradingViewWidget from 'react-tradingview-widget';
import { Themes } from 'react-tradingview-widget';
import axios from 'axios';
function ProfileChart({ selectedChart}) {
    const base = "http://127.0.0.1:5000/cryptocurrency/quotes/latest"
    const [info, setInfo] = useState({})
    useEffect(() => {
        
        axios.get(base + "?symbol="+selectedChart)
        .then(res=>{
            const {data} = res.data;
            setInfo(data);
            console.log(info, res);
            
        }).catch(err=>{
            console.log(err);
            
        })
    }, [selectedChart])    
    return (
        <div className="card profile_chart transparent">
            <div className="card-body">
                {/* <AreaChart /> */}
                <TradingViewWidget 
                    symbol={selectedChart}
                    theme={Themes.DARK}
                    locale="fa_IR"
                    width={"100%"}
                    height={400}
                    hide_top_toolbar={true}
                    allow_symbol_change={false}
                    withdateranges={true}
                    />
                <div className="chart-content text-center mt-3">
                    <div className="row">
                        <div className="col-xl-4 col-sm-6 col-6">
                            
                                <div className="chart-stat">
                                    <p className="mb-1">حجم 24 ساعته</p>
                                    { Object.keys(info).length > 0? 
                                        <strong dir="ltr">{info[selectedChart]["quote"]["USD"]["volume_24h"]}$</strong>
                                        :"در حال دریافت"
                                    }
                                </div>
                                
                        </div>
                        <div className="col-xl-4 col-sm-6 col-6">
                            <div className="chart-stat">
                                <p className="mb-1">حجم بازار</p>
                                { Object.keys(info).length > 0? 
                                    <strong  dir="ltr">{info[selectedChart]["quote"]["USD"]["market_cap"]} $</strong>
                                    :"در حال دریافت"
                                }
                            </div>
                        </div>
                        <div className="col-xl-4 col-sm-6 col-6">
                            <div className="chart-stat">
                                <p className="mb-1">درصد تغییر 1 ساعته</p>
                                { Object.keys(info).length > 0? 
                                    <strong dir="ltr">{info[selectedChart]["quote"]["USD"]["percent_change_1h"]}%</strong>
                                    :"در حال دریافت"
                                }
                            </div>
                        </div>
                        <div className="col-xl-4 col-sm-6 col-6">
                            <div className="chart-stat">
                            <p className="mb-1">درصد تغییر 7 روزه</p>
                                { Object.keys(info).length > 0? 
                                    <strong dir="ltr">{info[selectedChart]["quote"]["USD"]["percent_change_7d"]}%</strong>
                                    :"در حال دریافت"
                                }
                            </div>
                        </div>
                        <div className="col-xl-4 col-sm-6 col-6">
                            <div className="chart-stat">
                                <p className="mb-1">قیمت</p>
                                { Object.keys(info).length > 0? 
                                    <strong dir="ltr">{info[selectedChart]["quote"]["USD"]["price"]}%</strong>
                                    :"در حال دریافت"
                                }
                            </div>
                        </div>
                        <div className="col-xl-4 col-sm-6 col-6">
                            <div className="chart-stat">
                                <p className="mb-1">فعالیت معالمه</p>
                                <strong>70% buy </strong>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default ProfileChart
